package ra.dev.model.dto.request.orderDetail;import lombok.Data;import lombok.NoArgsConstructor;@Data@NoArgsConstructorpublic class OrderGetDetail {    private Integer detailId;    private Integer orderId;    private Integer price;    private String productName;    private Integer quantity;    private Integer totalAmount;    public OrderGetDetail(Integer detailId,                          Integer orderId,                          Integer price,                          String productName,                          Integer quantity,                          Integer totalAmount) {        this.detailId = detailId;        this.orderId = orderId;        this.price = price;        this.productName = productName;        this.quantity = quantity;        this.totalAmount = totalAmount;    }}