package ra.dev.model.entity;import com.fasterxml.jackson.annotation.JsonBackReference;import com.fasterxml.jackson.annotation.JsonIgnore;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import javax.persistence.*;import java.util.ArrayList;import java.util.List;@Entity@Table(name = "Catalog")@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class Catalog {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "CatalogID")    private int catalogID;    @Column(name = "CatalogName")    private String catalogName;    @Column(name = "CatalogStatus")    private boolean catalogStatus;    @OneToMany(mappedBy = "catalog")    @JsonIgnore    private List<Product> productList = new ArrayList<>();}