package ra.dev.model.entity;import com.fasterxml.jackson.annotation.JsonBackReference;import com.fasterxml.jackson.annotation.JsonFormat;import com.fasterxml.jackson.annotation.JsonIgnore;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import javax.persistence.*;import java.time.LocalDate;import java.util.ArrayList;import java.util.Date;import java.util.List;@Entity@Table(name = "Orders")@AllArgsConstructor@NoArgsConstructor@Getter@Setterpublic class Order {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "OrderID")    private int orderID;    @ManyToOne    @JoinColumn(name = "UserID")    @JsonIgnore    private Users userID;    @Column(name = "Date")    @JsonFormat(pattern = "dd/MM/yyyy")    private LocalDate date;    @Column(name = "TotalAmount")    private int totalAmount;    @Column(name = "Note")    private String note;    @Column(name = "FullName")    private String fullName;    @Column(name = "Address", columnDefinition = "text")    private String address;    @Column(name = "PhoneNumber")    private String phoneNumber;    @Column(name = "Email")    private String email;    @Column(name = "OrderStatus")    private int orderStatus;    @OneToMany(mappedBy = "order")    @JsonIgnore    private List<OrderDetail> listOrderDetail = new ArrayList<>();}