package ra.dev.model.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import javax.persistence.*;@Entity@Table(name = "OrderDtail")@Getter@Setter@AllArgsConstructor@NoArgsConstructorpublic class OrderDetail {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "DetailID")    private int detailID;    @ManyToOne    @JoinColumn(name = "Product")    private Product productID;    @ManyToOne    @JoinColumn(name = "Orders")    private Order order;    @Column(name = "Quantity")    private int quantity;    @Column(name = "Note")    private String note;    @Column(name = "Price")    private int price;    @Column(name = "TotalAmount")    private int totalAmount;}