package ra.dev.model.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import javax.persistence.*;@Entity@Table(name = "Star")@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class Star {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "StarID")    private int starID;    @ManyToOne(fetch = FetchType.EAGER)    @JoinColumn(name = "UserID")    @JsonIgnore    private Users userID;    @ManyToOne(fetch = FetchType.EAGER)    @JoinColumn(name = "OrderID")    @JsonIgnore    private Order order;    @Column(name = "FullName")    private String fullName;    @Column(name = "ProductID")    @JsonIgnore    private int productID;    @Column(name = "ProductName")    private String productName;    @Column(name = "NumberStar")    private int numberStar;    @Column(name = "StarStatus")    @JsonIgnore    private boolean starStatus;}