package ra.dev.model.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import javax.persistence.*;@Entity@Setter@Getter@AllArgsConstructor@NoArgsConstructor@Table(name = "SubImage")public class SubImage {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "SubID")    private int subID;    @Column(name = "SubLinks")    private String subLink;    @ManyToOne    @JoinColumn(name = "productID")    @JsonIgnore    private Product product;    @Override    public String toString() {        return this.subLink;    }}