package ra.dev.model.repository;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import ra.dev.model.entity.Cart;import java.util.List;@Repositorypublic interface CartRepository extends JpaRepository<Cart, Integer> {    @Query(value = "select o from Cart o where o.userID =:userID ")    List<Cart> searchCartByUserID(@Param("userID") Integer userUD);}