package ra.dev.model.serviceImp;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import ra.dev.model.dto.request.product.ViewByCatalog;import ra.dev.model.entity.Catalog;import ra.dev.model.repository.CatalogRepository;import ra.dev.model.service.CatalogService;import javax.transaction.Transactional;import java.sql.SQLException;import java.util.List;@Service@Transactional(rollbackOn = SQLException.class)public class CatalogServiceImp implements CatalogService {    @Autowired    CatalogRepository catalogRepository;    @Override    public List<Catalog> findAll() {        return catalogRepository.getAllCatalogActive();    }    @Override    public Catalog findByID(int catalogID) {        return catalogRepository.findById(catalogID).get();    }    @Override    public Catalog saveOrUpdate(Catalog catalog) {        return catalogRepository.save(catalog);    }    @Override    public void delete(int catalogID) {        catalogRepository.deleteById(catalogID);    }    @Override    public List<Catalog> searchByName(String catalogName) {        return catalogRepository.searchByNameAAndCatalogID(catalogName);    }}