package ra.dev.model.serviceImp;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import ra.dev.model.entity.OrderDetail;import ra.dev.model.repository.OrderDetailRepository;import ra.dev.model.service.OrderDetailService;import javax.transaction.Transactional;import java.sql.SQLException;import java.util.List;@Service@Transactional(rollbackOn = SQLException.class)public class OrderDetailServiceImp implements OrderDetailService {    @Autowired    OrderDetailRepository detailRepository;    @Override    public List<OrderDetail> findAll() {        return detailRepository.findAll();    }    @Override    public OrderDetail findByID(int orderDetailID) {        return detailRepository.findById(orderDetailID).get();    }    @Override    public OrderDetail saveOrUpdate(OrderDetail orderDetail) {        return detailRepository.save(orderDetail);    }    @Override    public void delete(int orderDetailID) {        detailRepository.deleteById(orderDetailID);    }    @Override    public List<OrderDetail> searchByName(String orderDetail) {        return null;    }}