package ra.dev.model.serviceImp;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import ra.dev.model.entity.Order;import ra.dev.model.repository.OrderRepository;import ra.dev.model.service.OrderService;import javax.transaction.Transactional;import java.sql.SQLException;import java.util.List;@Service@Transactional(rollbackOn = SQLException.class)public class OrderServiceImp implements OrderService {    @Autowired    OrderRepository orderRepository;    @Override    public List<Order> findAll() {        return orderRepository.findAll();    }    @Override    public List<Order> listStatus3() {        return orderRepository.status3();    }    @Override    public List<Order> listStatus4() {        return orderRepository.status4();    }    @Override    public List<Order> listStatus2() {        return orderRepository.status2();    }    @Override    public List<Order> listStatus1() {        return orderRepository.status1();    }    public Order findByIDUser(int userID) {        return orderRepository.findByUserID_UserId(userID);    }    @Override    public List<Order> getOrderByUserID(int userID) {        return orderRepository.getOrderByUserID(userID);    }    @Override    public List<Order> getOrderConfirm(int userID) {        return orderRepository.getOrderConfirm(userID);    }    @Override    public Order findByID(int orderID) {        return orderRepository.findById(orderID).get();    }    @Override    public Order saveOrUpdate(Order order) {        return orderRepository.save(order);    }    @Override    public void delete(int orderID) {        orderRepository.deleteById(orderID);    }    @Override    public List<Order> searchByName(String order) {        return null;    }}