package ra.dev.model.serviceImp;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import ra.dev.model.dto.request.star.getStar;import ra.dev.model.entity.Order;import ra.dev.model.entity.Product;import ra.dev.model.entity.Star;import ra.dev.model.repository.StarRepository;import ra.dev.model.service.StarService;import javax.transaction.Transactional;import java.sql.SQLException;import java.util.List;@Service@Transactional(rollbackOn = SQLException.class)public class StarServiceImp implements StarService {    @Autowired    StarRepository starRepository;    @Override    public List<Star> findAll() {        return starRepository.findAll();    }    @Override    public Star findByID(int starID) {        return starRepository.findById(starID).get();    }    @Override    public Star saveOrUpdate(Star star) {        return starRepository.save(star);    }    @Override    public List<Star> listStar() {        return starRepository.listStarConfirm();    }    @Override    public Star findByIDProduct(int productID) {        return starRepository.findByProductID(productID);    }    @Override    public void delete(int starID) {        starRepository.deleteById(starID);    }    @Override    public List<Star> searchByName(String starID) {        return null;    }    @Override    public List<Order> findByUserID(int userID) {        return starRepository.findByUserID_UserId(userID);    }}