package ra.dev.model.serviceImp;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import ra.dev.model.dto.request.product.ListSubImage;import ra.dev.model.entity.SubImage;import ra.dev.model.repository.SubImageRepository;import ra.dev.model.service.SubImageService;import javax.transaction.Transactional;import java.sql.SQLException;import java.util.List;@Service@Transactional(rollbackOn = SQLException.class)public class SubImageServiceImp implements SubImageService {    @Autowired    SubImageRepository subImageRepository;    @Override    public List<SubImage> findAll() {        return subImageRepository.findAll();    }    @Override    public SubImage findByID(int subImageID) {        return subImageRepository.findById(subImageID).get();    }    @Override    public ListSubImage save(ListSubImage subImage) {        return subImageRepository.save(subImage);    }    @Override    public ListSubImage update(ListSubImage ListSubImage) {        return subImageRepository.save(ListSubImage);    }    @Override    public SubImage saveOrUpdate(SubImage subImage) {        return subImageRepository.save(subImage);    }    @Override    public void delete(int subImageID) {        subImageRepository.deleteById(subImageID);    }    @Override    public List<SubImage> searchByName(String subImage) {        return null;    }}