package ra.dev.model.serviceImp;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import ra.dev.model.entity.Users;import ra.dev.model.repository.UserRepository;import ra.dev.model.service.UserService;import javax.transaction.Transactional;import java.sql.SQLException;import java.util.List;@Service@Transactional(rollbackOn = SQLException.class)public class UserServiceImp implements UserService {    @Autowired    UserRepository userRepository;    @Override    public List<Users> findAll() {        return userRepository.findAll();    }    @Override    public Users findByEmail(String email) {        return userRepository.findByEmail(email);    }    @Override    public Users findByID(int userID) {        return userRepository.findById(userID).get();    }    @Override    public Users saveOrUpdate(Users user) {        return userRepository.save(user);    }    @Override    public void delete(int userID) {        userRepository.deleteById(userID);    }    @Override    public List<Users> searchByName(String userName) {        return null;    }    @Override    public Users findByUserName(String userName) {        return userRepository.findByUserName(userName);    }    @Override    public boolean existsByUserName(String userName) {        return userRepository.existsByUserName(userName);    }    @Override    public boolean existsByEmail(String email) {        return userRepository.existsByEmail(email);    }}